import React, { Component,useState } from "react";

import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import { isEmail } from "validator";
//import DatePicker from 'react-date-picker';

import AuthService from "../services/auth.service";

const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const email = value => {
  if (!isEmail(value)) {
    return (
      <div className="alert alert-danger" role="alert">
        This is not a valid email.
      </div>
    );
  }
};

const vusername = value => {
  if (value.length < 3 || value.length > 20) {
    return (
      <div className="alert alert-danger" role="alert">
        The username must be between 3 and 20 characters.
      </div>
    );
  }
};

const vpassword = value => {
  if (value.length < 6 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The password must be between 6 and 40 characters.
      </div>
    );
  }
};

export default class Register extends Component {
  constructor(props) {
    super(props);
    this.handleRegister = this.handleRegister.bind(this);

    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.onChangeUserFirstname = this.onChangeUserFirstname.bind(this);
    this.onChangeUserLastname = this.onChangeUserLastname.bind(this);
    this.onChangeDob = this.onChangeDob.bind(this);
    this.onChangeSex= this.onChangeSex.bind(this);
    this.onChangeUserType = this.onChangeUserType.bind(this);
    this.onChangeUserUserId = this.onChangeUserUserId.bind(this);

    this.state = {
      UserId:"",
      firstname: "",
      lastname: "",
      dob:"",
      usertype:"",
      email: "",
      password: "",
      sex:"",
      successful: false,
      message: ""
    };
  }

  onChangeUserUserId(e) {
    this.setState({
      UserId: e.target.value
    });
  }

  onChangeUserFirstname(e) {
    this.setState({
      firstname: e.target.value
    });
  }

  onChangeUserLastname(e) {
    this.setState({
      lastname: e.target.value
    });
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    });
  }

  onChangeEmail(e) {
    this.setState({
      email: e.target.value
    });
  }

  onChangePassword(e) {
    this.setState({
      password: e.target.value
    });
  }

  onChangeDob(e) {
    this.setState({
      dob: e.target.value
    });
  }

  onChangeSex(e) {
    this.setState({
      sex: e.target.value
    });
  }

  onChangeUserType(e) {
    this.setState({
      usertype: e.target.value
    });
  }

  handleRegister(e) {
    e.preventDefault();

    this.setState({
      message: "",
      successful: false
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      AuthService.register(
        this.state.firstname,
        this.state.lastname,
        this.state.dob,
        this.state.email,
        this.state.usertype,
        this.state.sex,
        this.state.password,
        this.state.UserId

      ).then(
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            successful: false,
            message: resMessage
          });
        }
      );
    }
  }

  render() {

    return (

      <div className="col-md-12">
        <div className="card card-container">

          <Form
            onSubmit={this.handleRegister}
            ref={c => {
              this.form = c;
            }}
          >
            {!this.state.successful && (
              <div>

              <div className="form-group">
                  <label htmlFor="email">UserId</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="email"
                    value={this.state.UserId}
                    onChange={this.onChangeUserUserId}

                  />
                </div>

                <div className="form-group">
                  <label htmlFor="username">FirstName</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="username"
                    value={this.state.firstname}
                    onChange={this.onChangeUserFirstname}
                    validations={[required, vusername]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="email">LastName</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="email"
                    value={this.state.lastname}
                    onChange={this.onChangeUserLastname}
                    validations={[required, email]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="email">DOB</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="email"
                    value={this.state.dob}
                    onChange={this.onChangeDob}
                  />
                </div>


                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="email"
                    value={this.state.email}
                    onChange={this.onChangeEmail}
                    validations={[required, email]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="email">UserType</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="email"
                    value={this.state.usertype}
                    onChange={this.onChangeUserType}
                  />
                </div>


                <div className="form-group">
                  <label htmlFor="email">Sex</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="email"
                    value={this.state.sex}
                    onChange={this.onChangeSex}
                  />
                </div>


                <div className="form-group">
                  <label htmlFor="password">Password</label>
                  <Input
                    type="password"
                    className="form-control"
                    name="password"
                    value={this.state.password}
                    onChange={this.onChangePassword}
                    validations={[required, vpassword]}
                  />
                </div>

                <div className="form-group">
                  <button className="btn btn-primary btn-block">Sign Up</button>
                </div>
              </div>
            )}

            {this.state.message && (
              <div className="form-group">
                <div
                  className={
                    this.state.successful
                      ? "alert alert-success"
                      : "alert alert-danger"
                  }
                  role="alert"
                >
                  {this.state.message}
                </div>
              </div>
            )}
            <CheckButton
              style={{ display: "none" }}
              ref={c => {
                this.checkBtn = c;
              }}
            />
          </Form>
        </div>
      </div>
    );
  }
}
